INSERT INTO `ke_question_comments` VALUES (1,21,42,'你把两侧端口的主要配置拷贝下来看看',1480934496),(2,21,36,'@42:\n5566上的链路聚合配置如下：\nInterface trunk 1/3\nJoin vlan 100,90,1000-1999,2000-2999 tagged\nBRAS上的链路聚合配置如下：\nInterface gei-0/0/0/5\nSmartgroup 2 mode active\nInterface gei-0/0/0/6\nSmargroup 2 mode active ',1480934630),(3,24,54,'上位机和下位机采用的是什么通信接口，232？',1481163306),(4,42,160,'你们是做什么用，是生产还是专业伴奏，还是实验？',1484535906);
