INSERT INTO `ke_users_attrib` VALUES (3,3,NULL,'',0,''),(4,4,NULL,'',0,''),(6,6,NULL,'人中在腹部的位置',0,''),(7,7,NULL,'该感谢谁呢',0,''),(8,8,NULL,'',0,''),(9,9,NULL,'',0,''),(10,10,NULL,'give him a black eye',0,''),(11,11,NULL,'祸福无门，惟人自召;善恶之报，如影随形',0,''),(12,12,NULL,'你生命的前半辈子或许属于别人，活在别人的认为里。那把后半辈子还给你自己，去追随你内在的声音',0,''),(13,13,NULL,'鼓足勇气，换个金元帅的头型',0,''),(14,14,NULL,'冬天能穿多少穿多少，夏天能穿多少穿多少',0,''),(15,15,NULL,'你给我马不停蹄的滚开',0,''),(16,16,NULL,'没有什么比弱者对强者的鄙视更无力了',0,''),(17,17,NULL,'笨鸟先飞，笨猪先肥',0,''),(18,18,NULL,'',0,''),(19,19,NULL,'执子之手，将子拖走',0,''),(20,20,NULL,'如果错了，停止就是进步',0,''),(21,21,NULL,'笨鸟先飞，笨猪先肥',0,''),(22,22,NULL,'怕爹是孝顺，怕老婆是爱情。',0,''),(23,23,NULL,'低头要有勇气，抬头要有底气',0,''),(24,24,NULL,'世上没有绝望的处境，只有对处境绝望的人',0,''),(25,25,NULL,'酒逢千杯知已少',0,''),(26,26,NULL,'小时候，幸福是简单的事；长大了，简单是幸福的事',0,''),(27,27,NULL,'能来做爱，能去做菜；带得出去，带得回来',0,''),(28,28,NULL,'如何用一根绳子让围起来的圆最大？我跳进去，说，我在圆的外面。',0,''),(29,29,NULL,'我们身边就能找到宇宙的起源',0,''),(30,30,NULL,'革命的种子已在搓衣板上萌芽',0,''),(31,31,NULL,'生活将我们磨圆，是为了让我们滚得更远！',0,''),(32,32,NULL,'靖哥哥，黄蓉死了',0,''),(33,33,NULL,'比萨斜塔旁边有一个雕像，其贡献远超比萨，但远没有斜塔扬名，他就是斐波那契。',0,''),(34,34,NULL,'被抢走的东西就不要去惦记了，因为能抢走的东西都是垃圾',0,''),(35,35,NULL,'咸鱼翻身，还是咸鱼',0,''),(36,36,NULL,'有谁见过真正的牛奶，告诉我什么样的',0,''),(37,37,NULL,'我舍友给我安利',0,''),(38,38,NULL,'人类前进的两面大旗，一个是科学，一个是神学。',0,''),(39,39,NULL,'不要太高估古人的智慧。古典的精髓在于环境造就的思维',0,''),(40,40,NULL,'哥的小姨子的老公的舅舅的女儿今天向我表白了',0,''),(41,41,NULL,'多少年了都没有再出现像陈景润，茅盾等响当当的名字，有名气的大学回想一下，依然是民国时期遗留下来的。',0,''),(42,42,NULL,'香港的崛起，是在英国管辖期间，97以后，落寞的不止是香港的影视',0,''),(43,43,NULL,'',0,''),(44,44,NULL,'班主任来啦！班主任来啦！',0,''),(45,45,NULL,'英雄不问出路',0,''),(46,46,NULL,'分泌相同的荷尔蒙，你很可能会爱上一只狗。',0,''),(47,47,NULL,'左面是天堂，右面是我家',0,''),(48,48,NULL,'不知道现在的镖局里有没有人会功夫的，开电动三轮的倒是不少。',0,''),(49,49,NULL,'Be remembered, forget the forgotten, the change can change, and accept change',0,''),(50,50,NULL,'我的意中人是个绝色大美女，终于有一天她会骑着喷火的恐龙来嫁给我  的，可是我看见了她的坐骑，却没有看见主人',0,''),(51,51,NULL,'色欲二字生死之根',0,''),(52,52,NULL,'可以肯定的是未来一定会变化，否则现在就是未来。',0,''),(53,53,NULL,'没有不透风的墙，没有不能上吊的梁',0,''),(54,54,NULL,'直播剁吊，竖着剁。',0,''),(55,55,NULL,'世界这么大，江湖在哪里，我要去江湖',0,''),(56,56,NULL,'镜破不改光，兰死不改香',0,''),(57,57,NULL,'教育落后、物质匮乏，古人的素质和现代人比起来真不知道是什么样的',0,''),(58,58,NULL,'上天给你什么，就享受什么',0,''),(59,59,NULL,'是市场经济的确很好，还是计划经济没有被发展好？',0,''),(60,60,NULL,'没有什么可以永垂不朽，也许没有',0,''),(61,61,NULL,'爱不重不生娑婆，念不一不生净土。',0,''),(62,62,NULL,'形如匀速运动的假设，资本主义必然灭亡是在理想假设的前提下推断的结论',0,''),(63,63,NULL,'被老师打了，还会不会不告诉父母。',0,''),(64,64,NULL,'经变频器控制的电机旋转后，放置电机的台架发生振动，如何解决？',0,''),(65,65,NULL,'',0,''),(66,66,NULL,'转身时，请一定要优雅',0,''),(67,67,NULL,'世界太大，世界太大',0,''),(68,68,NULL,'一念愚即般若绝，一念智即般若生。',0,''),(69,69,NULL,'相哥；标签：远离女司机，珍爱',0,''),(70,70,NULL,'出售旧书，《葵花宝典》、《演员的自我修养》、《红楼梦》后四十回。',0,''),(71,71,NULL,'重温了《射雕》《神雕侠侣》《天龙八部》《独孤求败》《笑傲江湖》，厉害吧，觉得自己很熟悉，其实有很多细节都已经忘了。',0,''),(72,72,NULL,'吃完饭就困，就想睡了。',0,''),(73,73,NULL,'',0,''),(74,74,NULL,'随和而安',0,''),(75,75,NULL,'小女叩见大人',0,''),(76,76,NULL,'你不是丑，只是美的不明显。',0,''),(77,77,NULL,'择一城终老（句号）',0,''),(78,78,NULL,'霍金说世界上最难理解的是女人，这是神学还是科学的定论。',0,''),(79,79,NULL,'连头痛都是偏的',0,''),(80,80,NULL,'头顶大棉帽，身穿大花袄，脚蹬虎头鞋，手拿大花炮。',0,''),(81,81,NULL,'有事请到御膳房',0,''),(82,82,NULL,'别高谈论阔，一套房子就能消灭你的梦想',0,''),(83,83,NULL,'熊掌、鹿仁得先吃起来，然后嘛再看情况',0,''),(84,84,NULL,'',0,''),(85,85,NULL,'能说点什么呢',0,''),(86,86,NULL,'无论过去发生过什么，最好的尚未到来',0,''),(87,87,NULL,'Sometimes our vision clears only after our eyes are washed away with tears',0,''),(88,88,NULL,'荡气回肠，消化不良',0,''),(89,89,NULL,'Don\'t try so hard, the best things come when you least expect them to.',0,''),(90,90,NULL,'八戒，别以为你站在路灯下就是夜明猪了',0,''),(91,91,NULL,'',0,''),(92,92,NULL,'因为你是女人我才不杀你，不要以为我怕了你',0,''),(93,93,NULL,'房产确是地区发展状况的写照，并不是民生状况，两者不相干。',0,''),(94,94,NULL,'“国家”本身就意味着分裂，这是动物猎食属性延展。',0,''),(95,95,NULL,'用圆规和直尺可以将一个角三等分，确实可以。',0,''),(96,96,NULL,'吵架就花钱，看他还敢不敢吵架',0,''),(97,97,NULL,'百度本身并不值得过分恭维，其所拥有的大数据已属国家机密，即使百度要死，也不会留下全尸。',0,''),(98,98,NULL,'一念起，万水千山；一念灭，沧海桑田',0,''),(99,99,NULL,'政界的黔驴技穷必然会导致某层面的失控，房产、教育等无不如是。',0,''),(100,100,NULL,'黄昏独倚朱阑。西南新月眉弯。砌下落花风起，罗衣特地春寒。',0,''),(101,101,NULL,'怒为万障之根，忍为百福之首',0,''),(102,102,NULL,'杰出青年队队长。',0,''),(103,103,NULL,'女人腰细，所以长命',0,''),(104,104,NULL,'两人各自说着各家的家长里短，说的不好，结了仇，改日见面打了起来，这个就是政治和军事。',0,''),(105,105,NULL,'May the force be with you.',0,''),(106,106,NULL,'中国是目前全世界最安全的国家',0,''),(107,107,NULL,'政国尚庶',0,''),(108,108,NULL,'众将士放心杀敌，吾等在后方为你们镇后。',0,''),(109,109,NULL,'宰相必起于州部，猛将必发于卒伍',0,''),(110,110,NULL,'',0,''),(111,111,NULL,'作为失败的典型，我实在是太成功了',0,''),(112,112,NULL,'歌词“一颗芝麻糕，不止一针细”是什么歌里的',0,''),(113,113,NULL,'动物是如何看待人类的，是恨之入骨蓄谋称霸，还是甘愿俯首称臣，生之安之？',0,''),(114,114,NULL,'钻石恒久远，一颗就破产！',0,''),(115,115,NULL,'承诺于事情的发生而不是它如何发生。',0,''),(116,116,NULL,'听说舞乐传奇很好看诶，赶明儿瞅瞅！',0,''),(117,117,NULL,'20年内，国产电影没落不会摆脱平庸',0,''),(118,118,NULL,'想了半天也不知道说什么',0,''),(119,119,NULL,'希望似火，失望如烟，人生就是七处点火，八处冒烟',0,''),(120,120,NULL,'发怒一分钟，便失去60秒的幸福；快乐懂得分享，才能加倍快乐',0,''),(121,121,NULL,'光屁股推磨',0,''),(122,122,NULL,'色欲二字生死之根',0,''),(123,123,NULL,'房子乱啊，机器人佣人什么时候才能问世，急死了',0,''),(124,124,NULL,'孔子不能解决的问题，老子帮你解决',0,''),(125,125,NULL,'听说温格签了个下岗工人，不用猜，这肯定是真事',0,''),(126,126,NULL,'不要为明天忧虑，因为明天自有明天的忧虑，一天的难处一天当就够了。（马太福音6：34）',0,''),(127,127,NULL,'拉咋呼，哦哦，拉咋呼，这是什么歌里的',0,''),(128,128,NULL,'上天给你什么，就享受什么',0,''),(129,129,NULL,'就是经过，你别慌',0,''),(130,130,NULL,'黑洞里到底有什么，到底有什么，到底有什么！',0,''),(131,131,NULL,'金融危机能袭击国内，证明我们用的正是资本主义的那套东西。还是社会主义本身也会产生经济危机。',0,''),(132,132,NULL,'金小胖是我唯一的偶像',0,''),(133,133,NULL,'佛经是根据是么产生的呢',0,''),(134,134,NULL,'命由己造，相由心生，世间万物皆是化相，心不动，万物皆不动，心不变，万物皆不变。',0,''),(135,135,NULL,'总有几个蠢货，是我不能抛弃的朋友',0,''),(136,136,NULL,'江湖险恶，不行就撤',0,''),(137,137,NULL,'古代小偷是一个职业，可以得到御封的',0,''),(138,138,NULL,'我静静地走，正如我静静地来，谁要是打扰我，跳起来骂。',0,''),(139,139,NULL,'拿你的承诺去喂狗，发现狗死了',0,''),(140,140,NULL,'过去的一页，能不翻就不要翻，翻落了灰尘会迷了双眼',0,''),(141,141,NULL,'女为悦己者容，男为悦己者穷',0,''),(142,142,NULL,'让梦想成真的最好办法就是醒来',0,''),(143,143,NULL,'唾沫是用来数钞票的，而不是用来讲道理的',0,''),(144,144,NULL,'司马迁遭到流放，后代为了躲避追杀，把司马两字拆开，司字加了一竖，马字加了两点，作为新的姓氏，一个就是同，一个就是冯，鄙人姓同。',0,''),(145,145,NULL,'社会在先，而国家在后；社会是统一的，而国家却各自为政',0,''),(146,146,NULL,'努力赚钱吧，科技会让我们想尽办法的长生不老，没有钱，买命都不可能。',0,''),(147,147,NULL,'周末我和妈妈来到公园，公园里人头攒动，摩肩接踵。',0,''),(148,148,NULL,'你信了我也不信',0,''),(149,149,NULL,'',0,''),(150,150,NULL,'我只是一朵向日葵，执着的等待着属于我唯一的那缕阳光',0,''),(151,151,NULL,'给一个足够的撬杠，也未必能撬起地球',0,''),(152,152,NULL,'',0,''),(153,153,NULL,'雪花纷飞，飞，飞满天',0,''),(154,154,NULL,'玉皇和阎王是兄弟还是仇家。',0,''),(155,155,NULL,'行是现人的释怀，知是逝者的成悟',0,''),(156,156,NULL,'不戚戚于贫贱，不汲汲于富贵',0,''),(157,157,NULL,'下定决心要摆脱温四爷的噩梦，见了4我都犯晕',0,''),(158,158,NULL,'我黑白的世界在遇到你之后，全黑了！',0,''),(159,159,NULL,'需要你指点,但不需要你指指点点',0,''),(160,160,NULL,'多少人死在了所谓的“金句良言”里。',0,''),(161,161,NULL,'我一失足成大瘸子，再回首又闪了腰',0,''),(162,162,NULL,'见了哈特蒙一脚，见了布拉沃射正就行。',0,''),(163,163,NULL,'幸福毕竟不是曹操，怎能说到就到。',0,''),(164,164,NULL,'再一次面对你无知的冲动',0,''),(165,165,NULL,'怀念辉煌时的港区影视歌',0,''),(166,166,NULL,'https://www.acheterviagrafr24.com/',NULL,NULL),(167,167,NULL,'虽不安分，也该守己',0,''),(168,168,NULL,'如果觉得自己有气质，那就偏离的气质的本质',0,''),(169,169,NULL,'在东北，有一种运动叫滚犊子。',0,''),(170,170,NULL,'明不明白，我已经不是神仙了',0,''),(171,171,NULL,'怀念三鹿，引领了时代潮流',0,''),(172,172,NULL,'自然数集合和自然数的平方集合，哪个更大',0,''),(173,173,NULL,'天山老妖，法力无边',0,''),(174,174,NULL,'不怕脸着地，就怕去不了韩国',0,''),(175,175,NULL,'范厨师终于退休了。',0,''),(176,176,NULL,'失败是成功的后母',0,''),(177,177,NULL,'经济是国家的奴隶，人民是经济的奴隶。',0,''),(178,178,NULL,'此乃冲灵剑法',0,''),(179,179,NULL,'社会主义好，社会主义好',0,''),(180,180,NULL,'庸人的政策只适用于今天的社会，伟人的政策是对未来的规整。',0,''),(181,181,NULL,'天下还是不识君',0,''),(182,182,NULL,'没有金庸、古龙、梁晓声，江湖就没有了。',0,''),(183,183,NULL,'我要骗你，我身上你想要什么我就割下来给你',0,''),(184,184,NULL,'',0,''),(185,185,NULL,'做梦是意识去了另外一个地方，存不存在谁知道。',0,''),(186,186,NULL,'在记忆里，有一些瞬间，经历时没什么特别，回想时，却胜过千言万语',0,''),(187,187,NULL,'',0,''),(188,188,NULL,'80后互联网+营销',0,''),(189,189,NULL,'我是风，我还是风',0,''),(190,190,NULL,'是网',0,''),(191,191,NULL,'',NULL,NULL),(192,192,NULL,'',NULL,NULL),(193,193,NULL,'',NULL,NULL),(194,194,NULL,'',0,''),(195,195,NULL,'',NULL,NULL),(196,196,NULL,'',NULL,NULL),(197,197,NULL,'',NULL,NULL);
